import { useState } from "react";
import "./App.css";
function App() {
  // when typing anything in input field is the input text
  const [inputText , changeInputText] = useState('')
  // the input text should be inserted within a array
  //requirement : array 
  const [previousArray , changePreviousArray] = useState([''])
  const insertUserInput = (e) =>{
    changeInputText
  }
  return (
    <div className="App">
      <div class="container">
        <header class="header">
          <h1>ToDoApp</h1>
          <nav class="filter-nav">
            <button class="filter-btn" data-filter="all">
              All
            </button>
            <button class="filter-btn" data-filter="active">
              Active
            </button>
            <button class="filter-btn" data-filter="completed">
              Completed
            </button>
          </nav>
        </header>
        <div class="todo-input-container">
          <input
            value={inputText}
            type="text"
            class="todo-input"
            placeholder="Add a new task..."
          />
          <button class="add-btn" onClick={insertUserInput}>Add</button>
        </div>
        <ul class="todo-list">
          <li class="todo-item">
            <input type="checkbox" class="todo-checkbox" />
            <span class="todo-text">{previousArray}</span>
            <button class="delete-btn">Delete</button>
          </li>
        </ul>
        <footer class="footer">
          <span class="remaining-tasks">3 items left</span>
        </footer>
      </div>
    </div>
  );
}

export default App;
