import { useState } from "react";
import "./App.css";

function App() {
  // State for input text
  const [inputText, setInputText] = useState('');
  // State for the array of to-do items
  const [todoArray, setTodoArray] = useState([]);

  // Handle input field changes
  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  // Handle adding a new to-do item
  const addTodo = () => {
    if (inputText.trim()) {
      const newTodo = {
        id: Math.random().toString(36).substr(2, 9), // Unique ID
        text: inputText.trim(),
      };
      setTodoArray([...todoArray, newTodo]);
      setInputText(''); // Clear the input field
    }
  };

  // Handle deleting a to-do item
  const deleteTodo = (id) => {
    const newTodoArray = todoArray.filter(todo => todo.id !== id);
    setTodoArray(newTodoArray);
  };

  return (
    <div className="App">
      <div className="container">
        <header className="header">
          <h1>ToDoApp</h1>
          <nav className="filter-nav">
            <button className="filter-btn" data-filter="all">All</button>
            <button className="filter-btn" data-filter="active">Active</button>
            <button className="filter-btn" data-filter="completed">Completed</button>
          </nav>
        </header>
        <div className="todo-input-container">
          <input
            value={inputText}
            onChange={handleInputChange}
            type="text"
            className="todo-input"
            placeholder="Add a new task..."
          />
          <button className="add-btn" onClick={addTodo}>Add</button>
        </div>
        <ul className="todo-list">
          {todoArray.map(todo => (
            <li key={todo.id} className="todo-item">
              <input type="checkbox" className="todo-checkbox" />
              <span className="todo-text">{todo.text}</span>
              <button className="delete-btn" onClick={() => deleteTodo(todo.id)}>Delete</button>
            </li>
          ))}
        </ul>
        <footer className="footer">
          <span className="remaining-tasks">{todoArray.length} items left</span>
        </footer>
      </div>
    </div>
  );
}

export default App;
